import {assert, elementUpdated, fixture, html} from "@open-wc/testing";
import * as sinon from "sinon";
import {Et2TrackerAssigned} from "../Et2TrackerAssigned";

// Stub global egw
// @ts-ignore
window.egw = {
	image: () => "data:image/svg+xml;base64,",
	lang: i => i + "*",
	tooltipUnbind: () => {},
	webserverUrl: "",
	preference: () => "",
	decodePath: (path) => path
};

let element : Et2TrackerAssigned;

async function before()
{
	// Create an element to test with, and wait until it's ready
	// @ts-ignore
	element = await fixture<Et2TrackerAssigned>(html`
        <et2-tracker-assigned label="I select assigned users">
        </et2-tracker-assigned>
	`);

	// Stub egw()
	// @ts-ignore
	sinon.stub(element, "egw").returns(window.egw);
	await elementUpdated(element);

	return element;
}

describe("Tracker assigned select", () =>
{
	// Setup run before each test
	beforeEach(before);

	// Make sure it works
	it('is defined', () =>
	{
		assert.instanceOf(element, Et2TrackerAssigned);
	});

	it('has a label', async() =>
	{
		element.label = "Label set";
		await elementUpdated(element);

		assert.equal(element.select.shadowRoot.querySelector("[name='label']").textContent, "Label set");
	});

	it("starts empty", () =>
	{
		assert.notExists(element.querySelector("option"), "Static option not found in DOM");
		assert.deepEqual(element.select_options, [], "Unexpected option(s)");
	});
});
